var Emitter = require('../').Emitter

var emitter = new Emitter()

// once
emitter.on(
  'super'
, function onSuper (one, two) {
    console.log('on:super', one, two)

    emitter.off('super', onSuper)
  }
, function () {
    emitter.emit('super', 1, 2)
  }
)

emitter.pon(
  'cali:*'
, function onCali (channel, one, two, three) {
    console.log('pon:cali:*', channel, one, two, three)
  }
, function subbed () {
    emitter.emit('cali:test', 1, 2, 3)
    emitter.emit('cali:test', 1, 2, 3)
  }
)

emitter.pon(
  'cali:*'
, function onCali (channel, one, two, three) {
    console.log('pon2:cali:*', channel, one, two, three)
    emitter.premoveAllListeners('cali:*')
  }
)
